let geocoder,destination,origin;document.addEventListener("DOMContentLoaded",function(){function e(e){geocoder.geocode({address:e},function(e){console.log(e);var t=e[0].geometry.location.lat(),o=e[0].geometry.location.lng();document.getElementById("job_dest_lat").value=t,document.getElementById("job_dest_lng").value=o})}function t(e){geocoder.geocode({address:e},function(e){var t=e[0].geometry.location.lat(),o=e[0].geometry.location.lng();document.getElementById("job_origin_lat").value=t,document.getElementById("job_origin_lng").value=o})}geocoder=new google.maps.Geocoder,destination=document.getElementById("job_destination"),origin=document.getElementById("job_origin"),document.getElementById("geocoder_submit").addEventListener("click",function(o){o.preventDefault(),e(destination.value),t(origin.value),document.getElementById("job_date").style.visibility="visible",document.getElementById("job_time").style.visibility="visible",document.getElementById("bike_select").style.visibility="visible",document.getElementById("submit_post").style.visibility="visible",document.getElementById("geocoder_submit").style.display="none"})});